-module(util).
-export([long_string/0, get_random_string/2]).

long_string() ->
  <<"">>.

get_random_string(Length, AllowedChars) ->
    random:seed(erlang:now()),
    lists:foldl(fun(_, Acc) ->
                        [lists:nth(random:uniform(length(AllowedChars)),
                                   AllowedChars)]
                            ++ Acc
                end, [], lists:seq(1, Length)).
